import React, { Component } from 'react'
import UserLoggedTabs from '../../layout/tabs/UserLoggedTabs';
import axios from 'axios'

class HotelPretraga extends Component {
    state1={
        hoteli: []
    }
    state = {

    }

    handleSubmit = (e) => {
        e.preventDefault();
    }

    componentDidMount() {
        axios.get('http://localhost:8092/api/hotel/hotel/all')
            .then(res => {
                console.log(res);
                this.setState1({
                    hoteli: res.data
                })
            })
    }

    render() {
        return (
            <div>
                <UserLoggedTabs></UserLoggedTabs>
                <div className="container">
                    <form className="white" onSubmit={this.handleSubmit} >
                        <h2 className="red-text lighten-1 center">Dodavanje novog tipa sobe</h2>
                        <div className="container">
                            <div className="input-field">
                                <label htmlFor="nazivTipaDodaj">Naziv tipa:</label>
                                <input type="text" id='nazivTipaDodaj' />
                            </div>
                            <div className="input-field">
                                <label htmlFor="kapacitetDodaj">Kapacitet:</label>
                                <input type="number" id='kapacitetDodaj'/>
                            </div>
                            <div class="input-field col s12">
                                <select className="browser-default">
                                    const {hoteli} = this.state1;
                                    const hoteliList = hoteli.length ? (
                                        <option value="" disabled selected>Hotel:</option>
                                        hoteli.map(hotel =>{
                                            <option defaultValue={hotel.id}>hotel.name</option>
                                        })
                                    ) : (
                                        <option value="" disabled selected>Hotel:</option>
                                    )   
                                </select>
                            </div>
                            <div className="input-field">
                                <button className="btn blue lighten-1 z-depth-0">Pretra≈æi</button>
                            </div>
                        </div>
                    </form>
                </div>
            </div>
        )
    }
};

export default HotelPretraga;
