import React, { Component } from "react"

class ChangeCarDiscount extends Component {

    state = {

        id: -1,
        carId: {
            id: -1,
            rentPrice: -1,
            carType: {
                id: -1,
                numberOfSeats: '',
                modelYear: '',
                model: '',
                brand: '',
                carType: ''
            },
            rentService: {
                id: -1,
                name: '',
                adress: '',
                description: ''
            },
            branchOffice: {
                id: -1,
                name: '',
                adress: '',
                city: '',
                rentServiceDTO: {
                    id: '',
                    adress: '',
                    name: '',
                    description: ''
                }
            }
        },
        dateFrom: '',
        dateTo: '',
        carDiscountPrecentage: '',
        datumPocetka: {},
        datumKraja: {},
        vremePocetka: {},
        vremeKraja: {}

    }

    componentDidMount() {

        if (this.props.carDiscount) {
            var Pocetak = this.props.carDiscount.dateFrom.split("T");
            var Kraj = this.props.carDiscount.dateTo.split("T")


            this.setState(
                {
                    id: this.props.carDiscount.id,
                    carId: {
                        id: this.props.carDiscount.carId.id,
                        rentPrice: -1,
                        carType: {
                            id: this.props.carDiscount.carId.carType.id,
                            numberOfSeats: '',
                            modelYear: '',
                            model: '',
                            brand: '',
                            carType: ''
                        },
                        rentService: {
                            id: this.props.carDiscount.carId.rentService.id,
                            name: '',
                            adress: '',
                            description: ''
                        },
                        branchOffice: {
                            id: this.props.carDiscount.carId.branchOffice.id,
                            name: '',
                            adress: '',
                            city: '',
                            rentServiceDTO: {
                                id: '',
                                adress: '',
                                name: '',
                                description: ''
                            }
                        }
                    },
                    dateFrom: this.props.carDiscount.dateFrom,
                    dateTo: this.props.carDiscount.dateTo,
                    carDiscountPrecentage: this.props.carDiscount.carDiscountPrecentage,
                    datumPocetka: Pocetak[0],
                    datumKraja: Kraj[0],
                    vremePocetka: Pocetak[1],
                    vremeKraja: Kraj[1]
                }
            );
        }


    }

    handleChange = (e) => {
        this.setState({
            [e.target.id]: e.target.value
        })
    }

    handleDateTimeChange = (e) => {

        if (e.target.id == "datumPocetka" || e.target.id == "vremePocetka") {

            const datumPocetka = '';
            const deljenjeVremena1 = '';
            if (e.target.id == "datumPocetka") {
                datumPocetka = e.targetvalue.split("/");
                deljenjeVremena1 = this.state.vremePocetka.split(" ");
            }
            else {
                datumPocetka = this.state.datumPocetka.split("/");
                deljenjeVremena1 = e.targetvalue.split(" ");
            }

            const formatDatumaZaApi = datumPocetka[2] + '-' + datumPocetka[1] + '-' + datumPocetka[0];


            const deljenjeVremena2 = deljenjeVremena1[0].split(":");
            //ukoliko je PM onda dodajemo na sat 12
            if (deljenjeVremena1[1] == "PM") {
                deljenjeVremena2[0] = deljenjeVremena2[0] + 12;
            }

            const formatVremenaZaApi = deljenjeVremena2[0] + ':' + deljenjeVremena2[1] + ':00';

            const formatDatumIVremeZaApi = formatDatumaZaApi + 'T' + formatVremenaZaApi;

            this.setState({
                [e.target.id]: e.target.value

            })

        }

        this.setState({

        })
    }

    handleCarChange = (e) => {
        this.setState({

        })
    }


    handleSubmit = (e) => {
        e.preventDefault();
        if (this.props.izmena) {

            // this.props.editCarType(this.props.carType.id, this.state);
        }
        else {
            // this.props.createCarType(this.state);
            this.setState({
                id: -1,
                carId: {
                    id: -1,
                    rentPrice: -1,
                    carType: {
                        id: -1,
                        numberOfSeats: '',
                        modelYear: '',
                        model: '',
                        brand: '',
                        carType: ''
                    },
                    rentService: {
                        id: -1,
                        name: '',
                        adress: '',
                        description: ''
                    },
                    branchOffice: {
                        id: -1,
                        name: '',
                        adress: '',
                        city: '',
                        rentServiceDTO: {
                            id: '',
                            adress: '',
                            name: '',
                            description: ''
                        }
                    }
                },
                dateFrom: '',
                dateTo: '',
                carDiscountPrecentage: '',
                datumPocetka: {},
                datumKraja: {},
                vremePocetka: {},
                vremeKraja: {}
            })
        }
    }

    deleteCarDiscount = (id) => {

        //  this.props.deleteCarType(id);

    }



    render() {
        return (
            <div className="card indigo lighten-1">
                <div className="card-content">
                    <div className="card">
                        <div className="card-content">
                            <span className="card-title indigo-text lighten-1 left"><strong>{this.props.carDiscount ? this.props.carDiscount.id : ''}</strong></span>
                            {
                                this.props.brisanje ?
                                    <span className="card-title right" onClick={() => this.deleteCarDiscount(this.props.carDiscount.id)}><a class="btn-floating btn-small waves-effect waves-light red"><i>x</i></a></span>
                                    :
                                    ''
                            }
                            <div className="container">
                                <form className="white" onSubmit={this.handleSubmit} >
                                    <div class="input-field col s12">
                                        <select className="browser-default" onChange={this.handleCarChange}>

                                            {this.props.cars.map(car => {
                                                return (
                                                    <option selected={this.props.carDiscount ? car.id == this.props.carDiscount.carId.id : 'false'} value={car.id}>Id vozila: {car.id}, Cena: {car.rentPrice}</option>
                                                );
                                            })}
                                        </select>
                                    </div>
                                    <div className="input-field">
                                        <label htmlFor="carDiscountPrecentage">Popust</label>
                                        <input type="number" id='carDiscountPrecentage' onChange={this.handleChange} defaultValue={this.props.carDiscount ? this.props.carDiscount.carDiscountPrecentage : ''} />
                                    </div>

                                    <div>
                                        <label htmlFor="datumPocetka" className="active">Datum pocetka popusta</label>
                                        <input type="date" id='datumPocetka' value={this.state.datumPocetka} />
                                    </div>

                                    <div>
                                        <label htmlFor="vremePocetka" className="active">Vreme pocetka popusta</label>
                                        <input type="time" id='vremePocetka' value={this.state.vremePocetka} />
                                    </div>


                                    <div>
                                        <label htmlFor="datumKraja" className="active">Datum kraja popusta</label>
                                        <input type="date" id='datumKraja' value={this.state.datumKraja} />
                                    </div>

                                    <div>
                                        <label htmlFor="vremeKraja" className="active">Vreme kraja popusta</label>
                                        <input type="time" id='vremeKraja' value={this.state.vremeKraja} />
                                    </div>

                                    {this.props.izmena ?
                                        <div className="input-field">
                                            <button className="btn blue lighten-1 z-depth-0">Azuriraj</button>
                                        </div>
                                        :
                                        <div className="input-field">
                                            <button className="btn green darken-3 z-depth-0">Dodaj</button>
                                        </div>
                                    }
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
};

export default ChangeCarDiscount;
