import React, { Component } from 'react'
import "../neregistrovan.css";
import axios from 'axios';
import { NavLink } from "react-router-dom"


class RentACarIzlistavanje extends Component {

    state = {
        servisi: [],
        aktivnaPretraga: false
    }

    componentDidMount() {
        axios.get('http://localhost:8090/api/rentacar/rentACarService/all')//tvoj url
            .then(res => {
                console.log(res);
                this.setState({
                    servisi: res.data
                })
            })
    }

    handleVozilaClick = (serviceId) => {
        this.props.history.push('/vozila/' + serviceId)
    }

    handleFilijalaClick = (serviceId) => {
        this.props.history.push('/filijale/' + serviceId)
    }

    toggleForm = () => {
        this.setState({
            aktivnaPretraga: true
        })
    }

    hideForm = () => {
        this.setState({
            aktivnaPretraga: false
        })
    }

    render() {
        const { servisi } = this.state;
        const servisiList = servisi.length ? (servisi.map(servis => {
            return (
                <div className="post card grey lighten-2">
                    <div className="card-content container">
                        <span className="card-title center">{servis.name}</span>
                        <div className="left-align">
                            <p>Adresa: {servis.adress}</p>
                            <p>Opis: {servis.description}</p>
                            <button className="buttons btn-small waves-effect waves-light indigo right" onClick={() => this.handleVozilaClick(servis.id)}>Vozila</button>
                            <button className="buttons btn-small waves-effect waves-light indigo right" onClick={() => this.handleFilijalaClick(servis.id)}>Filijale</button>
                        </div>
                    </div>
                </div>
            )
        })) : (<div className="center">Nije pronadjen nijedan servis.</div>)

        return (
            <div>
                <div className="container center">
                    <h2 className="red-text lighten-1 center">Lista servisa</h2>
                    {
                        this.state.aktivnaPretraga ?
                            <NavLink to="#" onClick={this.toggleForm}>Pretrazite servise</NavLink>
                            :
                            <div>
                                <NavLink to="#" onClick={this.hideForm}>Sakrij formu</NavLink>
                                <div className="card">
                                    <div className="card-content">
                                        <span className="card-title indigo-text lighten-1 left"><strong>{this.props.carType ? this.props.carType.id : ''}</strong></span>
                                        {this.props.brisanje ?
                                            <span className="card-title right" onClick={() => this.deleteCarType(this.props.carType.id)}><a class="btn-floating btn-small waves-effect waves-light red"><i>x</i></a></span>
                                            :
                                            ''
                                        }


                                        <div className="container">
                                            <form className="white" onSubmit={this.handleSubmit}>
                                                <div className="input-field">
                                                    <label htmlFor="brand">Brend</label>
                                                    <input type="text" onChange={this.handleChange} id='brand' defaultValue={this.props.carType ? this.props.carType.brand : ''} />
                                                </div>
                                                <div className="input-field">
                                                    <label htmlFor="model">Model</label>
                                                    <input type="text" onChange={this.handleChange} id='model' defaultValue={this.props.carType ? this.props.carType.model : ''} />
                                                </div>
                                                <div className="input-field">
                                                    <label htmlFor="modelYear">Godina proizvodnje</label>
                                                    <input type="number" onChange={this.handleChange} id='modelYear' defaultValue={this.props.carType ? this.props.carType.modelYear : ''} />
                                                </div>
                                                <div className="input-field">
                                                    <label htmlFor="carType">Vrsta auta</label>
                                                    <input type="text" onChange={this.handleChange} id='carType' defaultValue={this.props.carType ? this.props.carType.carType : ''} />
                                                </div>
                                                <div className="input-field">
                                                    <label htmlFor="numberOfSeats">Broj sedista</label>
                                                    <input type="number" onChange={this.handleChange} id='numberOfSeats' defaultValue={this.props.carType ? this.props.carType.numberOfSeats : ''} />
                                                </div>
                                                {this.props.izmena ?
                                                    <div className="input-field">
                                                        <button className="btn blue lighten-1 z-depth-0">Azuriraj</button>
                                                    </div>
                                                    :
                                                    <div className="input-field">
                                                        <button className="btn green darken-3 z-depth-0">Dodaj</button>
                                                    </div>
                                                }
                                            </form>
                                        </div>
                                    </div>
                                </div>
                            </div>
                    }


                    {servisiList}
                </div>
            </div>
        )
    }
}

export default RentACarIzlistavanje;
